import { StyleSheet } from 'react-native';



const styles = StyleSheet.create({
  fundoContainer: {
    flex : 1,
    backgroundColor : "#219EBC",
    alignItems: 'center',
    justifyContent: 'center',
    },
    caixaPerfil: {
    borderRadius : 25,
    backgroundColor : "white",
    width : "80%",
    height : 380,
    alignItems: 'center',

},
 titulo: {
  marginTop: 25,
  fontSize: 30,
  color: "#219ebc",
  fontWeight: "bold",
  marginLeft: 10,
  alignItems: "center",
  justifyContent: "center"  
},

 caixaPerfil : {
   marginTop: 50,
   margin: "10%",
   alignItems:"center",
   width: "80%",
   height: 450,
   backgroundColor: "#fff",
   borderRadius: 20   
 },
 input : {
  width : "80%",
  borderStyle : "solid",
  borderColor : "black",
  borderWidth : 1,
  borderRadius : 10
},
corTexto :{
  color : "#219EBC",
  marginRight : "60%",
},

corTextoBold:{
  color : "#219EBC",
  marginRight : "60%",
  fontWeight: "bold",
  fontSize: 12
},
senha : {
  color : "#219EBC",
  fontSize : 12,
  paddingVertical : 10
},
button : {
  margin : 10,
  alignItems: 'center',
  borderRadius : 10,
  backgroundColor : "#FFB703",
  width : "80%",
  height : 25
},
buttonText:{
  color : "white",
  paddingTop : 2,
}
 

});

export default styles
