import React, {useState} from 'react';
import { StatusBar } from 'react-native';

import { NavigationContainer } from '@react-navigation/native';
import { createMaterialBottomTabNavigator } from '@react-navigation/material-bottom-tabs';
import Icon from 'react-native-vector-icons/Ionicons'

import Game from './Pages/Game';
import Login from './Pages/login';
import Home from './Pages/home/Home';
import Cadastro from './Pages/cadastro/Cadastro';
import AlterarPerfil from './Pages/alterar/AlterarPerfil';
import Perfil from './Pages/perfil/Perfil';
import RecuperarSenha from './Pages/recuperSenha/'

const Tab = createMaterialBottomTabNavigator();

function App() {
  return (
    
    <NavigationContainer>
      <StatusBar />

      <Tab.Navigator initialRouteName="Perfil" tabBarOptions={{labelStyles: {color:"white"}}}
      inactiveColor="rgba(255, 255, 255, 0.5)"
      activeColor="#fff" >

        <Tab.Screen name="Home" component={Home} 
        options={{
          tabBarLabel: 'Home',
          tabBarIcon: ({color}) => (
            <Icon name="home" size={25} color={"#FFF"} />
          )
        }} />
        
        <Tab.Screen name="AlterarPerfil" component={AlterarPerfil} 
        options={{
          tabBarLabel: 'Alterar Perfil',
          tabBarIcon: ({color}) => (
            <Icon name="close" size={25} color={"#FFF"} />
          )
        }} />
        <Tab.Screen name="Game" component={Game}
        options={{
          tabBarLabel: 'Play',
          tabBarIcon: ({color}) => (
            <Icon name="play" size={25} color={"#FFF"} />
          )
        }} />

        <Tab.Screen name="Login" component={Login}
        options={{
          tabBarLabel: 'Close',
          tabBarIcon: ({color}) => (
            <Icon name="close" size={25} color={"#FFF"} />
          )
        }} />
        <Tab.Screen name="Cadastro" component={Cadastro}
         options={{
          tabBarLabel: 'Close',
          tabBarIcon: ({color}) => (
            <Icon name="close" size={25} color={"#FFF"} />
          )
        }} />

        <Tab.Screen name="Perfil" component={Perfil}
         options={{
          tabBarLabel: 'Person',
          tabBarIcon: ({color}) => (
            <Icon name="person" size={25} color={"#FFF"} />
          )
        }} />

        <Tab.Screen name="RecuperarSenha" component={RecuperarSenha}
         options={{
          tabBarLabel: 'Person',
          tabBarIcon: ({color}) => (
            <Icon name="person" size={25} color={"#FFF"} />
          )
        }} /> 
      </Tab.Navigator>
      
    </NavigationContainer>
  );
}


export default App;
