import React from 'react';
import { View, TextInput, Text, ImageBackground, TouchableOpacity, Button, StyleSheet } from 'react-native';
import ImageVideo from '../../assets/videoImg.jpeg'


const supportedURL = "http://favelacao.epizy.com/favelacao/frontend/pages/sobreJogo.php";

const unsupportedURL = "http://favelacao.epizy.com/favelacao/frontend/pages/sobreJogo.php";

const URLButton = ({ url, children }) => {
  const handlePress = useCallback(async () => {
    
    const supported = await Linking.canOpenURL(url);

    if (supported) {
      
      await Linking.openURL(url);
    } else {
      Alert.alert(`NÃ£o sei como abrir este URL: ${url}`);
    }
  }, [url]);

  return <Button title={children} onPress={handlePress} />;
};

const Auda = () => {
  return (
    <View > 
    <View style={styles.fundoWhite}>
      <Text style={styles.titulo}>Ajuda</Text>
     
      <Image style={styles.ImageVideo} source={ImageVideo} ></Image>
    
    <URLButton style={styles.button} url={supportedURL}>Saiba Mais</URLButton>
      
    </View>
    
    <View style={styles.fundoBlue}>
    </View>
    
 </View>
  
  );
};



const styles = StyleSheet.create({
  fundoWhite: {     
      height:"40%",
      backgroundColor : "#fff",
      alignItems: 'center',
      justifyContent: 'center',
      },

  fundoBlue: {
    
    height:"60%",
    backgroundColor : "#219EBC",
    alignItems: 'center',
    justifyContent: 'center',
  },

  titulo: {
    marginTop: 25,
    fontSize: 30,
    color: "#219ebc",
    fontWeight: "bold",
    marginLeft: 10,
    textAlign: "center" 
  },

  viImageVideodeo: {
    borderColor: "black",
  }
      

  })


  export default Ajuda;
