import { StyleSheet } from 'react-native';



const styles = StyleSheet.create({
  fundoContainer: {
    flex : 1,
    backgroundColor : "#219EBC",
    alignItems: 'center',
    justifyContent: 'center',
    },

  caixa: {
    borderRadius : 25,
    backgroundColor : "white",
    width : "80%",
    height : 380,
    alignItems: 'center',

},

 titulo: {
  marginTop: 25,
  fontSize: 30,
  color: "#219ebc",
  fontWeight: "bold",
  marginLeft: 10,
  textAlign: "center" 
},


corTextoOne :{
  color : "#219EBC",
  textAlign: "center",
  fontSize: 12, 
},

corText :{
  color : "#219EBC",
  marginRight : "60%",
},

corTextoBold:{
  marginTop: 20,
  textAlign: "center",
  color : "#219EBC",
  fontWeight: "bold",
  fontSize: 15, 
},

corTextoBlack:{ 
  fontSize: 12, 
  textAlign: "center"
},
 
 input : {
  width : "80%",
  borderStyle : "solid",
  borderColor : "black",
  borderWidth : 1,
  borderRadius : 10
},


senha : {
  color : "#219EBC",
  fontSize : 12,
  paddingVertical : 10
},
button : {
  margin : 10,
  alignItems: 'center',
  borderRadius : 10,
  backgroundColor : "#FFB703",
  width : "80%",
  height : 25
},
buttonText:{
  color : "white",
  paddingTop : 2,
}
 

});

export default styles
