import { StyleSheet } from 'react-native';



const styles = StyleSheet.create({
 fundoContainer: {
   width: "100%",
   height: "100%",  
   backgroundColor: "#219ebc"
 },

 caixaPerfil : {
   marginTop: 50,
   margin: "10%",
   alignItems:"center",
   width: "80%",
   height: 450,
   backgroundColor: "#fff",
   borderRadius: 20   
 },

 imgBox:{
   marginTop: 40, 
   backgroundColor: "#219ebc",   
   width: 80,
   height: 80,
   borderRadius: 60,  
 },
 
 titulo: {
   marginTop: 25,
   fontSize: 30,
   color: "#219ebc",
   fontWeight: "bold",
   marginLeft: 10,
   alignItems: "center",
   justifyContent: "center"
   
 },

 nomeJogador: {
   color: "#023047",
   fontWeight: "bold",
   fontSize: 25,
   marginTop: 15,
   marginLeft: 10,
   alignItems: "center",
   justifyContent: "center"
   
 },

 margin: {
  marginTop: 20,
 },
 
 

 caixaMedalhas: {
 
  marginLeft: -90,
  flexDirection: 'row',
  justifyContent: 'space-around',  
 },

 texto: {
  
  marginTop: 8,  
  fontSize: 12,
  color: "#219ebc", 

 },
 input: {
   backgroundColor: "#f8f9fa",
   width: 200,
   margin: 2,
   borderWidth: 0.3,
  },
  

 she: {
   width: 80,
   height: 80,
   borderRadius: 60,   
 },
 

});

export default styles
